<#if tableName?? >CREATE TABLE ${tableName}(<#list fields as item><#if item_index != 0>,</#if> [${item.COL_NAME}]  <#if item.COL_TYPE =="nvarchar" || item.COL_TYPE =="varchar">text<#else>${item.COL_TYPE}</#if><#if item.COL_TYPE !="image" && item.COL_TYPE !="datetime" && item.COL_TYPE!="ntext" && item.COL_TYPE!="int" && item.COL_TYPE !="tinyint">(<#if item.COL_LEN lt 1073741823>${item.COL_LEN?string('#')}<#else>1073741823</#if>)	</#if> <#if item.COL_NULLABLE == 0 >NOT NULL</#if> <#if item.COL_PRIMARY?? > PRIMARY KEY </#if> <#if item.COL_DEFAULT_VALUES?? > DEFAULT ${item.COL_DEFAULT_VALUES}</#if> </#list>)</#if>